import React from 'react';
import PropTypes from 'prop-types';

function Image(props) {
  /* Each individual image
   */
  const {
    authorName, imgUrl, authorUrl, photoName
  } = props;
  return (
    <div class="photo">
      <a href={imgUrl}>
        <img src={imgUrl} alt={photoName}></img>
        <a href={authorUrl}>
          <h3 class="photographer">{authorName}</h3>
        </a>
      </a>
    </div>
  );
}

export class CommentSection extends React.Component {
  /* Display number of image and post owner of a single post
   */

  constructor(props) {
    // Initialize mutable state
    super(props);
    this.state = { comments: [], url: '' };
    this.handler = this.handler.bind(this);
  }

  componentDidMount() {
    // This line automatically assigns this.props.url to the const variable url
    const { url } = this.props;

    // Call REST API to get the post's information
    fetch(`${url}comments/`, { credentials: 'same-origin' })
      .then((response) => {
        if (!response.ok) throw Error(response.statusText);
        return response.json();
      })
      .then((data) => {
        const { comments } = this.state;
        for (let i = 0; i < data.comments.length; i += 1) {
          comments.push(data.comments[i]);
        }

        this.setState({
          comments,
          url: data.url,
        });
      })
      .catch((error) => console.log(error));
  }

  handler(data) {
    const { comments } = this.state;
    comments.push(data);
    this.setState({
      comments,
    });
  }

  renderComment(i) {
    const { comments } = this.state;
    const commentToRender = comments[i];

    return (
      <Comment
        key={(commentToRender.commentid).toString()}
        text={commentToRender.text}
        owner={commentToRender.owner}
        commentid={commentToRender.commentid}
        ownerShowUrl={commentToRender.owner_show_url}
      />
    );
  }

  render() {
    // This line automatically assigns this.state.imgUrl to the const variable imgUrl
    // and this.state.owner to the const variable owner
    const { comments, url } = this.state;

    const renderedComments = [];
    for (let i = 0; i < comments.length; i += 1) {
      renderedComments.push(this.renderComment(i));
    }

    // Render number of post image and post owner
    // this.state, this.text.value (params to handleKeyPress)
    return (
      <div className="CommentSection">
        { renderedComments }
        <CommentButton url={url} handler={this.handler} />
      </div>
    );
  }
}

CommentSection.propTypes = {
  url: PropTypes.string.isRequired,
};

Comment.propTypes = {
  owner: PropTypes.string.isRequired,
  ownerShowUrl: PropTypes.string.isRequired,
  text: PropTypes.string.isRequired,
};

export default CommentSection;
